/*=========================================================================
  FUNCTION:  qcril_qmi_nas_async_cb_helper

===========================================================================*/
/*!
    @brief
    Handle QCRIL_EVT_QMI_NAS_HANDLE_ASYNC_CB

    @return
    None.
*/
/*=========================================================================*/
void qcril_qmi_nas_async_cb_helper
(
  const qcril_request_params_type *const params_ptr,
  qcril_request_return_type *const ret_ptr
)
{
    RIL_Errno                               ril_req_res = RIL_E_GENERIC_FAILURE;
    qmi_resp_callback_type                  *qmi_resp = (qmi_resp_callback_type*) params_ptr->data;
    unsigned long                           msg_id;
    nas_avoid_tuneaway_resp_msg_v01         *tune_away_resp;
    nas_set_dual_standby_pref_resp_msg_v01  *dual_standby_resp;
    qcril_qmi_cb_func_ptr                   async_cb_func_ptr;
    RIL_Errno ril_err = RIL_E_GENERIC_FAILURE;
    qcril_request_params_type req_data;
    QCRIL_NOTUSED(ret_ptr);

    QCRIL_LOG_FUNC_ENTRY();

    if (qmi_resp)
    {
        msg_id = qmi_resp->msg_id;
        async_cb_func_ptr = (qcril_qmi_cb_func_ptr)qmi_resp->cb_data;
        memset(&req_data, 0, sizeof(req_data));

        switch(msg_id)
        {
            case QMI_NAS_DUAL_STANDBY_PREF_RESP_MSG_V01:
                dual_standby_resp = (nas_set_dual_standby_pref_resp_msg_v01*)qmi_resp->data_buf;
                if (dual_standby_resp)
                {
                    ril_req_res = qcril_qmi_util_convert_qmi_response_codes_to_ril_result(
                                                qmi_resp->transp_err, &dual_standby_resp->resp );
                    if( async_cb_func_ptr )
                    {
                        (async_cb_func_ptr)(ril_req_res);
                    }
                }
                break;

            case QMI_NAS_AVOID_TUNEAWAY_RESP_MSG_V01:
                tune_away_resp = (nas_avoid_tuneaway_resp_msg_v01*)qmi_resp->data_buf;
                if (tune_away_resp)
                {
                    ril_req_res = qcril_qmi_util_convert_qmi_response_codes_to_ril_result(
                                                qmi_resp->transp_err, &tune_away_resp->resp );
                    if( async_cb_func_ptr )
                    {
                        (async_cb_func_ptr)(ril_req_res);
                    }
                }
                break;

            case QMI_NAS_GET_SSAC_INFO_RESP_MSG_V01:
                memset(&req_data, 0, sizeof(req_data));
                ril_err = pack_request_data(qmi_resp, &req_data);
                if ( ril_err == RIL_E_SUCCESS )
                {
                    qcril_qmi_nas_ssac_info_resp_handler(&req_data);
                }

                break;

            case QMI_NAS_GET_SYS_INFO_RESP_MSG_V01:
                memset(&req_data, 0, sizeof(req_data));
                ril_err = pack_request_data(qmi_resp, &req_data);
                if ( ril_err == RIL_E_SUCCESS )
                {
                    qcril_qmi_nas_vops_info_resp_handler(&req_data);
                }
                break;

        }

        if (qmi_resp->data_buf)
        {
            qcril_free(qmi_resp->data_buf);
        }
    }

    QCRIL_LOG_FUNC_RETURN();
}

/*
 * Function does the following -
 * - checks the request list
 * - if id exists, packs the response with corresponding values
 */
RIL_Errno pack_request_data
(
    qmi_resp_callback_type * qmi_resp,
    qcril_request_params_type * req_data
)
{
    qcril_instance_id_e_type instance_id;
    uint32 user_data;
    uint16 req_id;
    qcril_reqlist_public_type req_info;
    RIL_Errno ril_err = RIL_E_GENERIC_FAILURE;

    QCRIL_ASSERT( qmi_resp->data_buf != NULL );
    user_data = ( uint32 )(uintptr_t) qmi_resp->cb_data;
    instance_id = QCRIL_EXTRACT_INSTANCE_ID_FROM_USER_DATA( user_data );
    req_id = QCRIL_EXTRACT_USER_ID_FROM_USER_DATA( user_data );

    if( qcril_reqlist_query_by_req_id( req_id, &instance_id, &req_info ) == E_SUCCESS )
    {
        if( qmi_resp->transp_err != QMI_NO_ERR )
        {
            QCRIL_LOG_ERROR("Transp error (%d) recieved from QMI for RIL request %d",
            qmi_resp->transp_err, req_info.request);
            /* Send GENERIC_FAILURE response */
            qcril_send_empty_payload_request_response( instance_id,
                    req_info.t,
                    req_info.request,
                    RIL_E_GENERIC_FAILURE );
            }
            else
            {
                req_data->t = req_info.t;
                req_data->event_id = req_info.request;
                req_data->modem_id = QCRIL_DEFAULT_MODEM_ID;
                req_data->instance_id = instance_id;
                req_data->datalen = qmi_resp->data_buf_len;
                req_data->data = qmi_resp->data_buf;
                ril_err = RIL_E_SUCCESS;
        }
    }
    else
    {
        QCRIL_LOG_ERROR("Req id: %d not for QMI_NAS_GET_SSAC_INFO_RESP_MSG_V01", req_id);
    }
    return ril_err;
}
