//==============================================================================
// Send Vops request to QMI
// Check if cache value exists. If it is present, pass cached value to Telephony
// Else send the request to QMI
//==============================================================================
void qcril_qmi_nas_vops_info_get_request
(
    const qcril_request_params_type *const params_ptr,
    qcril_request_return_type *const ret_ptr /*!< Output parameter */
)
{
    nas_get_sys_info_resp_msg_v01 * qmi_response = NULL;
    qcril_reqlist_public_type           reqlist_entry;
    RIL_Errno                           ril_err = RIL_E_GENERIC_FAILURE;
    bool is_vops_valid = false;
    bool vops = false;
    uint32 user_data;
    QCRIL_NOTUSED( ret_ptr );
    QCRIL_LOG_FUNC_ENTRY();

    NAS_CACHE_LOCK();

    is_vops_valid = nas_cached_info.voice_support_on_lte_valid;
    if (is_vops_valid)
    {
        vops = (bool)nas_cached_info.voice_support_on_lte;
    }
    NAS_CACHE_UNLOCK();

    // If cache value is present, pass the same to telephony
    // else send the request down to QMI.
    if (is_vops_valid)
    {
        qcril_qmi_nas_send_vops_info(params_ptr->t,
                                     vops);
        ril_err = RIL_E_SUCCESS;
    }
    else
    {
        qcril_reqlist_default_entry( params_ptr->t,
                                     params_ptr->event_id,
                                     params_ptr->modem_id,
                                     QCRIL_REQ_AWAITING_CALLBACK,
                                     QCRIL_EVT_NONE,
                                     NULL,
                                     &reqlist_entry );

        if ( qcril_reqlist_new( QCRIL_DEFAULT_INSTANCE_ID, &reqlist_entry ) == E_SUCCESS )
        {
            user_data = QCRIL_COMPOSE_USER_DATA( QCRIL_DEFAULT_INSTANCE_ID,
                    params_ptr->modem_id, reqlist_entry.req_id );

            qmi_response = qcril_malloc( sizeof( *qmi_response ) );
            if (qmi_response != NULL)
            {
                ril_err = qcril_qmi_client_send_msg_async_ex(QCRIL_QMI_CLIENT_NAS,
                                                             QMI_NAS_GET_SYS_INFO_REQ_MSG_V01,
                                                             NULL, NAS_NIL,
                                                             qmi_response,
                                                             sizeof(*qmi_response),
                                                             (void*)(uintptr_t)user_data);
            }
            else
            {
                QCRIL_LOG_ERROR("malloc failed on qcril_qmi_nas_vops_info_get_request");
            }
        }
        else
        {
            QCRIL_LOG_ERROR("adding to reqlist failed for qcril_qmi_nas_vops_info_get_request");
        }
    }

    if ( ril_err != RIL_E_SUCCESS )
    {
        if ( qmi_response != NULL )
        {
            qcril_free( qmi_response );
        }
        qcril_send_empty_payload_request_response( QCRIL_DEFAULT_INSTANCE_ID, params_ptr->t,
                params_ptr->event_id, RIL_E_GENERIC_FAILURE );

    }

    QCRIL_LOG_FUNC_RETURN_WITH_RET(ril_err);
}

