//==============================================================================
// Handle QMI Vops response for the GET request sent by RIL
//==============================================================================
void qcril_qmi_nas_vops_info_resp_handler
(
    const qcril_request_params_type *const params_ptr
)
{
    nas_get_sys_info_resp_msg_v01 *vops_info_resp;
    RIL_Errno ril_err = RIL_E_GENERIC_FAILURE;
    ims_VopsInfo vops;

    QCRIL_LOG_FUNC_ENTRY();

    if ( params_ptr->data != NULL )
    {
        vops_info_resp = (nas_get_sys_info_resp_msg_v01 *)params_ptr->data;
        ril_err = qcril_qmi_util_convert_qmi_response_codes_to_ril_result(
                  QMI_NO_ERR, &vops_info_resp->resp);

        if ( ril_err == RIL_E_SUCCESS )
        {
            QCRIL_LOG_INFO("RESP SUCCESS for VOPS REQUEST");

            NAS_CACHE_LOCK();

            memset(&vops, 0, sizeof(vops));

            // Update the cache for VOPS
            update_sys_info_cache_from_resp(vops_info_resp);

            QCRIL_LOG_DEBUG("Vops updated cache = %d, Old Vops value = %d",
                    nas_cached_info.voice_support_on_lte, old_voice_support_on_lte);

            QCRIL_LOG_DEBUG("Vops cache valid = %d" ,
                    NAS_CACHE_IS_ENTRY_VALID(nas_cached_info.voice_support_on_lte));

            if( NAS_CACHE_IS_ENTRY_VALID(nas_cached_info.voice_support_on_lte))
            {
                vops.isVopsEnabled = nas_cached_info.voice_support_on_lte;
                vops.has_isVopsEnabled = true;
                old_voice_support_on_lte = nas_cached_info.voice_support_on_lte;

                NAS_CACHE_UNLOCK();

                // Send the response to telephony
                imsRadioSendMessage(params_ptr->t,
                                          ims_MsgType_RESPONSE,
                                          ims_MsgId_REQUEST_GET_VOPS_INFO,
                                          ims_Error_E_SUCCESS,
                                          &vops,
                                          sizeof(vops));
            }
            else
            {
                NAS_CACHE_UNLOCK();
                ril_err = RIL_E_GENERIC_FAILURE;
                QCRIL_LOG_DEBUG( "VOPS TLV does not exist");
            }
        }
        else
        {
            QCRIL_LOG_DEBUG( "RESP ERROR for VOPS REQUEST" );
        }
    }
    else
    {
        QCRIL_LOG_DEBUG( "RESP DATA null for VOPS REQUEST" );
    }

    if ( ril_err == RIL_E_GENERIC_FAILURE )
    {
        qcril_send_empty_payload_request_response(QCRIL_DEFAULT_INSTANCE_ID,
                                                  params_ptr->t,
                                                  params_ptr->event_id,
                                                  ril_err);
    }
    qcril_reqlist_free( params_ptr->instance_id, params_ptr->t );
    QCRIL_LOG_FUNC_RETURN_WITH_RET(ril_err);
}

