//==============================================================================
// Handle QMI Ssac response for the GET request sent by RIL
//==============================================================================
void qcril_qmi_nas_ssac_info_resp_handler
(
   const qcril_request_params_type *const params_ptr
)
{
    nas_get_ssac_info_resp_msg_v01 *ssac_info_rep;
    ims_SsacInfo ssacInfo;
    RIL_Errno ril_err = RIL_E_GENERIC_FAILURE;

    QCRIL_LOG_FUNC_ENTRY();

    if ( params_ptr->data != NULL )
    {
        ssac_info_rep = (nas_get_ssac_info_resp_msg_v01 *)params_ptr->data;
        ril_err = qcril_qmi_util_convert_qmi_response_codes_to_ril_result(
                QMI_NO_ERR, &ssac_info_rep->resp);

        if ( ril_err == RIL_E_SUCCESS )
        {
            QCRIL_LOG_INFO("RESP SUCCESS for SSAC REQUEST");

            memset(&ssacInfo, 0, sizeof(ssacInfo));

            // Convert the Qmi response to proto response
            copy_qmi_ssac_info_resp_to_proto_ssac(ssac_info_rep, &ssacInfo);

            // Send the response to Telephony
            imsRadioSendMessage(params_ptr->t,
                                      ims_MsgType_RESPONSE,
                                      ims_MsgId_REQUEST_GET_SSAC_INFO,
                                      ims_Error_E_SUCCESS,
                                      &ssacInfo,
                                      sizeof(ssacInfo));
        }
        else
        {
            QCRIL_LOG_DEBUG("RESP ERROR for SSAC");
        }
    }
    else
    {
        QCRIL_LOG_DEBUG("Param data null for SSAC");
    }
    if (ril_err != RIL_E_SUCCESS)
    {
        qcril_send_empty_payload_request_response(QCRIL_DEFAULT_INSTANCE_ID,
                                                  params_ptr->t,
                                                  params_ptr->event_id,
                                                  RIL_E_GENERIC_FAILURE);
    }
    qcril_reqlist_free( params_ptr->instance_id, params_ptr->t );
    QCRIL_LOG_FUNC_RETURN();
}

//==============================================================================
// Convert Ssac QMI response to proto Ssac
//==============================================================================
void copy_qmi_ssac_info_resp_to_proto_ssac
(
    nas_get_ssac_info_resp_msg_v01 *qmiSsacInfo,
    ims_SsacInfo *protoSsacInfo
)
{
    QCRIL_LOG_FUNC_ENTRY();

    if( NAS_CACHE_IS_ENTRY_VALID(qmiSsacInfo->ssac_voice_info))
    {
        protoSsacInfo->has_barringFactorVoice = true;
        protoSsacInfo->barringFactorVoice = qmiSsacInfo->ssac_voice_info.barring_factor_voice;
        protoSsacInfo->has_barringTimeVoice = true;
        protoSsacInfo->barringTimeVoice = qmiSsacInfo->ssac_voice_info.barring_time_voice;
    }

    if( NAS_CACHE_IS_ENTRY_VALID(qmiSsacInfo->ssac_video_info))
    {
        protoSsacInfo->has_barringFactorVideo = true;
        protoSsacInfo->barringFactorVideo = qmiSsacInfo->ssac_video_info.barring_factor_video;
        protoSsacInfo->has_barringTimeVideo = true;
        protoSsacInfo->barringTimeVideo = qmiSsacInfo->ssac_video_info.barring_time_video;
    }

    if( NAS_CACHE_IS_ENTRY_VALID(qmiSsacInfo->sib2_ssac_voice_info))
    {
        protoSsacInfo->has_barringFactorVoiceSib = true;
        protoSsacInfo->barringFactorVoiceSib =
                qmiSsacInfo->sib2_ssac_voice_info.sib2_barring_factor_voice;
        protoSsacInfo->has_barringTimeVoiceSib = true;
        protoSsacInfo->barringTimeVoiceSib =
                qmiSsacInfo->sib2_ssac_voice_info.sib2_barring_time_voice;
    }

    if( NAS_CACHE_IS_ENTRY_VALID(qmiSsacInfo->sib2_ssac_video_info))
    {
         protoSsacInfo->has_barringFactorVideoSib = true;
         protoSsacInfo->barringFactorVideoSib =
                 qmiSsacInfo->sib2_ssac_video_info.sib2_barring_factor_video;
         protoSsacInfo->has_barringTimeVideoSib = true;
         protoSsacInfo->barringTimeVideoSib =
                 qmiSsacInfo->sib2_ssac_video_info.sib2_barring_time_video;
    }

    QCRIL_LOG_FUNC_RETURN();
}

